                                                        <tr className="bg-light">
                                                            <th>Type:</th>
                                                            <th>Company:</th>
                                                            <th>Exp date</th>
                                                        </tr>
                                                    </thead>
                                                    <tbody>
                                                        {driver.training.map(tr => (
                                                            <tr key={tr.type}>
                                                                <td>Type: {tr.type}: </td>
                                                                <td>{tr.company}</td>
                                                                <td>
                                                                    <DateCheck Date = {tr.end} />  
                                                                </td>
                                                            </tr>
                                                        ))}
                                                    </tbody>
                                                </Table>
                                            </Col>
                                            <Col >
                                                <p>Screening:</p>
                                                <Table bordered className="w-75">
                                                    <thead>
                                                        <tr className="bg-light">
                                                            <th>Type:</th>
                                                            <th>Exp date</th>
                                                        </tr>
                                                    </thead>
                                                    <tbody>
                                                        {driver.screenings.map(scr => (
                                                            <tr key={scr.type}>
                                                                <td>{scr.type}</td>
                                                                <td>
                                                                    <DateCheck Date = {scr.end} />
                                                                </td>
                                                            </tr>
                                                        ))}
                                                    </tbody>
                                                </Table>
                                            </Col>
                                        </Row>
                                    </div>
                                ))}
                            </TabPane>
                            <TabPane tabId="2">
                                <h2 className="my-5">Vehicles</h2>
                                {test2.vehicles.map(vehicle => (
                                    <div>

