import React, { Component } from 'react'
import Layout from '../components/layout'
import Layout2 from './tv_info/MyLayout.js'
import Link from 'next/link'
import fetch from 'isomorphic-unfetch'


const id = Batman;

const TvPage = props => (
    <Layout>
      <form action="http://api.tvmaze.com/search/shows?q=" alt="tv search" height="20">
      <input name="q" />
      <input type="submit" value="search" />
    </form>
        <Layout2>
          <h1>Batman TV Shows</h1>
            <ul>
              {props.shows.map(show => (
                <li key={show.id}>
                  <Link as={`/p/${show.id}`} href={`/tvpost?id=${show.id}`}>
                    <a>{show.name}</a>
                  </Link>
                </li>
                ))}
            </ul>
        </Layout2>
    </Layout>
  );

  TvPage.getInitialProps = async function() {
    const res = await fetch('https://api.tvmaze.com/search/shows?q={$id}');
    const data = await res.json();
  
    console.log(`Show data fetched. Count: ${data.length}`);
  
    return {
      shows: data.map(entry => entry.show)
    };
  };
  
  export default TvPage;
