import React, { Component } from 'react'
import fetch from 'isomorphic-unfetch';
//import { createSorter } from './sort';
//import data from './something.json';




class List extends Component {
  constructor(props) {
    super(props);
    const sorters = [{ property: 'name', direction: 'DESC' }];
    this.state = {
        
        sorters: this.props.sorters
    };
}

  static defaultProps = {
    sorters: [{
      property: 'name'
    }, {
      property: 'company'
    }]
  };

  componentDidMount() {
    const { sorters } = this.state;
    fetch('/something.json?sort=${JSON.stringify(sorter)}')
    .then(res => res.json())
    .then(this.onLoad.bind(this));
  }

  parseData(data) {
    const { sorters } = this.state;

    if (data && data.length) {
      if (Array.isArray(sorters) && sorters.length) {
        data.sort(createSorter(...sorters));
      }
    }

    return data;
  }

  onLoad = data => {
    this.setState({
      data: this.parseData(data)
    });
  };

  render() {
    const { data } = this.state;
    
    return data ? this.renderData(data) : this.renderLoading();
  }

  renderData(data) {
    if (data && data.length > 0) {
      return (
        <div>
          {data.map(item => (
            <div key={item.id}>
              <a href={`mailto:$(item.email)`}>{item.name}</a> {item.company}
              </div>
          ))}
        </div>
      );
    } else {
      return <div>No items found</div>;
    }
  }

  renderLoading() {
    return <div>Loading...</div>;
  }
}



