import React from 'react'
import PropTypes from 'prop-types'
import Layout from '../components/layout.js'
import CSS from '../components/style.css'
import Testapp5 from './test_items/transition_item.js'




const numbers = [1, 2, 3, 4, 5, 6];

var test = {
    str: 0,
    const: 0
}




function ListItem(props) {
    return <li>{props.value}</li>;
}

function NumberList(props) {
    const numbers = props.numbers;

    return(
        <ul>
            {numbers.map((number) =>
                <ListItem key={number.toString()}
                value={number} />
            )}
        </ul>
    );
}

/////////////////////////////////////////////////////////

class FixedTable extends React.Component {
    constructor(props) {
      super(props)
      
      this.state = {
        items: props.items,
        sortingProp: 'id',
        sortingDirectionAsc: true
      }
      
      this.scrollSidebar = this.scrollSidebar.bind(this)
      this.scrollBodyListHeader = this.scrollBodyListHeader.bind(this)
    }
    
    componentDidMount() {
      var $fixedTableBodyList = React.findDOMNode(this.refs.fixedTableBodyList)
      
      Ps.initialize($fixedTableBodyList)
      
      $fixedTableBodyList.addEventListener('ps-scroll-y', this.scrollSidebar)
      $fixedTableBodyList.addEventListener('ps-y-reach-start', () => this.scrollSidebar({target: { scrollTop: 0 }}))
      $fixedTableBodyList.addEventListener('ps-scroll-x', this.scrollBodyListHeader)
      $fixedTableBodyList.addEventListener('ps-x-reach-start', () => this.scrollBodyListHeader({target: { scrollLeft: 0 }}))
    }
    
    componentWillReceiveProps(nextProps) {
      this.setState({
        items: nextProps.items,
        sortingProp: 'id',
        sortingDirectionAsc: true
      })
    }
    
    scrollSidebar(event) {    
      React.findDOMNode(this.refs.sidebarList).style.top = `${-event.target.scrollTop}px`   
    }
    
    scrollBodyListHeader(event) {    
      React.findDOMNode(this.refs.bodyListHeader).style.left = `${-event.target.scrollLeft}px`
    }
    
    sortAsc(prop) {
      return this.state.items.sort((a, b) => {
        if (a[prop] > b[prop]) {
          return 1;
        }
  
        if (a[prop] < b[prop]) {
          return -1;
        }
  
        return 0;
      })
    }
    
    sortDesc(prop) {
      return this.state.items.sort((a, b) => {
        if (a[prop] < b[prop]) {
          return 1;
        }
  
        if (a[prop] > b[prop]) {
          return -1;
        }
  
        return 0;
      })
    }
    
    sortBy(prop) {
      if (prop === this.state.sortingProp) {
        if (this.state.sortingDirectionAsc) {
          this.setState({
            sortingDirectionAsc: false,
            items: this.sortDesc(prop)
          })
          return
        } else {
          this.setState({
            sortingDirectionAsc: true,
            items: this.sortAsc(prop)
          })
          return
        }
      }
      this.setState({
        sortingProp: prop,
        sortingDirectionAsc: true,
        items: this.sortAsc(prop)
      })
    }
    
    renderFixedList() {
      var items = this.state.items.map((i) => {
        return (
          <div className="fixed-table__row">
            <div className="fixed-table__col">{i.id}</div>
          </div>
        )
      })
      
      return (
        <div ref="sidebarList"
             className="fixed-table__list fixed-table__list--fixed">
          {items}
        </div>
      )
    }
    
    renderList() {
      var items = this.state.items.map((i, index) => {
        return (
          <div key={`details-row-${index}`}
               className="fixed-table__row">
            <div className="fixed-table__col">{i.Name}</div>
            <div className="fixed-table__col">{i.Region}</div>
            <div className="fixed-table__col">{i.County}</div>
            <div className="fixed-table__col">{i.Amb}</div>
            <div className="fixed-table__col">{i.WCHR}</div>
            <div className="fixed-table__col">{i.Stretcher}</div>
          </div>
        )
      })
      return (
        <div ref="fixedTableBodyList"
             onScroll="onBodyListScroll"
             className="fixed-table__list">
          {items}
        </div>
      )
    }
    render() {
      return (
        <div className="fixed-table">
          <div className="fixed-table__sidebar">
            <div className="fixed-table__header">
              <div className="fixed-table__th"
                   onClick={this.sortBy.bind(this, 'id')}>Id</div>
            </div>
            <div className="fixed-table__scrollable-sidebar">
              {this.renderFixedList()}
            </div>
          </div>
          <div className="fixed-table__body">
            <div className="fixed-table__header">
              <div ref="bodyListHeader"
                   className="fixed-table__scrollable-header">
                <div className="fixed-table__th"
                     onClick={this.sortBy.bind(this, 'Name')}>Provider Name</div>
                <div className="fixed-table__th"
                     onClick={this.sortBy.bind(this, 'Region')}>Region</div>
                <div className="fixed-table__th"
                     onClick={this.sortBy.bind(this, 'County')}>County</div>
                <div className="fixed-table__th"
                     onClick={this.sortBy.bind(this, 'AMB')}>Ambulatory</div>
                <div className="fixed-table__th"
                     onClick={this.sortBy.bind(this, 'WCHR')}>Wheelchair</div>
                <div className="fixed-table__th"
                     onClick={this.sortBy.bind(this, 'Stretcher')}>Stretcher</div>
              </div>
            </div>
            {this.renderList()}
          </div>
        </div>
      )
    }
  }
  
  FixedTable.propTypes = {
    items: React.PropTypes.array.isRequired
  }
  
  var items = require('./provider_data/provider.json');

////////////////////////////////////////////////////////////
class Testpage extends Component {
    constructor(props) {
        super(props);
        this.state = {str: test.str};
        this.setstr = this.setstr.bind(this);
        this.onChange = this.onChange.bind(this);
    }

    setstr() {
        //this.setState({str: Math.floor((Math.random() * 20) + 1)});
       test.str = Math.floor((Math.random() * 20) + 1);
       this.setState({str: test.str});
    }

    onChange() {
        this.setState({str: test.str});
    }
    state = {
        boxactive: false
    };

    switch = () => {
        this.setState(prevState => ({
            boxactive: !prevState.boxactive
        }));
    };

    render() {
        return (
            <Layout>
                <h1>This page is for testing and learning different concepts.</h1>
                <div>
                    <NumberList numbers={numbers} />
                </div>
                    <p>Click on the Google Image and hold down the mouse button:</p>
                    <div className="box pointer" onClick={this.switch}>
                    <img src="https://www.google.com/images/srpr/logo11w.png" width="100"/>
                     
                </div>
                <div className="Testapp5">
                    <Testapp5 />
                </div>
                <div>
                    <p>STR: {test.str}</p>
                    <p>str 2: {this.state.str}</p>
                    <button onClick={this.setstr}>Set Srength</button>
                </div>
                <div>
                    <p>importing local JSON file:</p>
                    <FixedTable />
                </div>
            </Layout>
        )
    }
}




export default Testpage;
