import React from 'react'



var Hero = {
    Str: 0,
    Const: Math.floor(Math.random() * 20) + 1,
    Dext: Math.floor(Math.random() * 20) + 1,
    HitPoints: 20
}

var Constadj = { one: -3, two: -2, three: -1, four: 0,
    five: 1, six: 2, seven: 3, eight: 4, nine: 5, ten: 6}

var AdjustedHp = (Hero.HitPoints) + (SetConstAdj());

function SetConstAdj() {
    if (Hero.Const == 1) {
        return Constadj.one;
    }
    if (Hero.Const == 2 && Hero.Const == 3) {
        return Constadj.two;
    }
    if (Hero.Const >= 4 && Hero.Const <= 6) {
        return Constadj.three;
    }
    if (Hero.Const >= 7 && Hero.Const <= 14) {
        return Constadj.four;
    }
    if (Hero.Const ==15) {
        return Constadj.five;
    }
    if (Hero.Const == 16) {
        return Constadj.six;
    }
    if (Hero.Const == 17) {
        return Constadj.seven;
    }
    if (Hero.Const == 18) {
        return Constadj.eight;
    }
    if (Hero.Const == 19) {
        return Constadj.nine;
    }
    if (Hero.Const == 20) {
        return Constadj.ten;
    }
}


function SetAttributes() {
    Hero.Str = Math.floor(Math.random() * 20) + 1;
}


class displayHero extends React.Component {
    render() {
        return (
            <div>
            <div>
                <p>Strength: {Hero.Str}</p>
                <p>Constitution: {Hero.Const}</p>
                <p>Dexterity: {Hero.Dext}</p>
                <p>Hitpoints: {AdjustedHp}</p>
            </div>
            <div>
            <button onClick={SetAttributes()}>Set Str</button>
            </div>
            </div>
        );
    }
}

export default displayHero;
