//sets hitpoints based on constitution stat number.

export function SetheroConstAdj(constitution) {
    if (constitution == 1) {
        return -3;
    }
    if (constitution == 2 || constitution == 3) {
        return -2;
    }
    if (constitution >= 4 && constitution <= 6) {
        return -1;
    }
    if (constitution >= 7 && constitution <= 14) {
        return 0;
    }
    if (constitution == 15) {
        return 1;
    }
    if (constitution == 16) {
        return 2;
    }
    if (constitution == 17) {
        return 3;
    }
    if (constitution == 18) {
        return 4;
    }
    if (constitution == 19) {
        return 5;
    }
    if (constitution == 20) {
        return 6;
    }
};
