import React, { Component } from 'react';
import { createFilter } from './Filter';
import { createSorter } from './Sort';
//import data from './something.json';

var data = require('./something.json');



class List extends Component {
  state = {
    data,
    sorters: this.props.sorters
  };
  
  static defaultProps = {
    sorters: [{
      property: 'name'
    }, {
      property: 'company'
    }]
  };
  
  


  componentDidMount () {
    const { sorters } = this.state
    fetch(`/data.json?sort=${JSON.stringify(sorters)}`)
      .then(res => res.json())
      .then(this.onLoad);
  }

  

  render () {
    const {data} = this.state;
    
      

      return (
        <div>
          {
            data.map(item => (
              <div key={item.id}>
                {item.id} <br />
                {item.company}
                <a href={`mailto:${item.email}`}>{item.name}</a> {item.company}
              </div>
            ))
          }
        </div>
      );
    } 
  }



export default List;
