import React, { Component } from 'react'

export function setHitAdj() {
    if (newHero.Str == 1) {
        return HitAdj.one;
    }
    if (newHero.Str == 2 || newHero.Str == 3) {
        return HitAdj.two;
    }
    if (newHero.Str == 4 || newHero.Str == 5) {
        return HitAdj.three;
    }
    if (newHero.Str == 6 || newHero.Str == 7) {
        return HitAdj.four;
    }
    if (newHero.Str >= 8 && newHero.Str <= 16) {
        return HitAdj.five;
    }
    if (newHero.Str == 17 ) {
        return HitAdj.six;
    }
    if (newHero.Str == 18 ) {
        return HitAdj.seven;
    }
    if (newHero.Str == 19 ) {
        return HitAdj.eight;
    }
    if (newHero.Str == 20 ) {
        return HitAdj.nine;
    }
}

export function setDmgAdj() {
    if (newHero.Str == 1) {
        return DamageAdj.one;
    }
    if (newHero.Str == 2 || newHero.Str == 3) {
        return DamageAdj.two;
    }
    if (newHero.Str == 4 || newHero.Str == 5) {
        return DamageAdj.three;
    }
    if (newHero.Str == 6 || newHero.Str == 7) {
        return DamageAdj.four;
    }
    if (newHero.Str >= 8 && newHero.Str <= 16) {
        return DamageAdj.five;
    }
    if (newHero.Str == 17 ) {
        return DamageAdj.six;
    }
    if (newHero.Str == 18 ) {
        return DamageAdj.seven;
    }
    if (newHero.Str == 19 ) {
        return DamageAdj.eight;
    }
    if (newHero.Str == 20 ) {
        return DamageAdj.nine;
    }
}

export function setacAdj() {
    if (newHero.Dext == 1 || newHero.Dext == 2) {
        return AcAdj.one;
    }
    if (newHero.Dext == 3) {
        return AcAdj.two;
    }
    if (newHero.Dext == 4) {
        return AcAdj.three;
    }
    if (newHero.Dext == 5) {
        return AcAdj.four;
    }
    if (newHero.Dext == 6) {
        return AcAdj.five;
    }
    if (newHero.Dext >= 7 && newHero.Dext <= 14) {
        return AcAdj.six;
    }
    if (newHero.Dext == 15) {
        return AcAdj.seven;
    }
    if (newHero.Dext == 16) {
        return AcAdj.eight;
    }
    if (newHero.Dext == 17) {
        return AcAdj.nine;
    }
    if (newHero.Dext == 18 || newHero.Dext == 19) {
        return AcAdj.ten;
    }
    if (newHero.Dext == 20) {
        return AcAdj.eleven;
    }
}

export function SetConstAdj() {
    if (newHero.Const == 1) {
        return Constadj.one;
    }
    if (newHero.Const == 2 || newHero.Const == 3) {
        return Constadj.two;
    }
    if (newHero.Const >= 4 && newHero.Const <= 6) {
        return Constadj.three;
    }
    if (newHero.Const >= 7 && newHero.Const <= 14) {
        return Constadj.four;
    }
    if (newHero.Const == 15) {
        return Constadj.five;
    }
    if (newHero.Const == 16) {
        return Constadj.six;
    }
    if (newHero.Const == 17) {
        return Constadj.seven;
    }
    if (newHero.Const == 18) {
        return Constadj.eight;
    }
    if (newHero.Const == 19) {
        return Constadj.nine;
    }
    if (newHero.Const == 20) {
        return Constadj.ten;
    }
}
