import React, { Component } from 'react'
import Layout from '../components/layout'
import Link from 'next/link'

const resumediv = styled.resume `
    float: 'right';
    width: 800;
    height: 400;
    border: '1px solid black';
    transform: translateY(${props => props.visible ? '0px' : '-50px'});
    transition: transform .5s ease-in;
`;

class Resume extends Component {
    constructor(props) {
        super(props);

        this.state = { enlargeresume: true};
    }

    componentDidMount() {
        window.addEventListener('click', () => {
            this.setState({ enlargeresume: window.innerHeight > 400});
        });
    }

    render() {
        return (
            <Layout>
                <resumediv visible={this.state.enlargerresume}>
                <div className="Resume-body">
                    <h1 className="center">Resume</h1>
                    <ul className="center">
                        <li className="Resume-li">Phone: (770) 316-4832</li>
                        <li className="Resume-li">Email: mark@fioca.com</li>
                        <li className="Resume-li">www.linkedin.com/in/mark-fioca</li>
                    </ul>
                </div>
                <div className="Resume-divider">
                    <p className="Resume-divider-text">PROFESSIONAL SUMMARY</p>
                </div>
                <div>
                    <p>
                    Dedicated and motivated customer relations professional 10+ years of experience in 
                    customer service and Administration.  Expertise in communication, analytical thinking 
                    and problem solving to maintain efficiency and productivity.  
                    </p>
                </div>
                </resumediv>
            </Layout>
        )
    }
}

export default Resume;
