import React from 'react'



var newHero = {
    Str: 0,
    Const: 0,
    Dext: 0,
    BaseHitPoints: 20,
    BaseArmorClass: 10,
    DamageAdj: 0,
    HitAdj: 0
};

var HeroHp = 0;
var HeroAc = 0;



var Constadj = { 
    one: -3, two: -2, three: -1, four: 0,
    five: 1, six: 2, seven: 3, eight: 4, nine: 5, ten: 6
};

var AcAdj = {
    one: -5, two: -4, three: -3, four: -2, five: -1, six: 0,
    seven: 1, eight: 2, nine: 3, ten: 4, eleven: 5
};


function setDmgAdj() {
    if (newHero.Str == 1) {
        return (newHero.DamageAdj = -4) && (newHero.HitAdj = -4);
    }
    if (newHero.Str == 2 && newHero.Str == 3) {
        return (newHero.DamageAdj = -3) && (newHero.HitAdj = -3);
    }
    if (newHero.Str == 4 && newHero.Str == 5) {
        return (newHero.DamageAdj = -2) && (newHero.HitAdj = -2);
    }
    if (newHero.Str == 6 && newHero.Str == 7) {
        return (newHero.DamageAdj = -1) && (newHero.HitAdj = -1);
    }
    if (newHero.Str >= 8 && newHero.Str ,+ 16) {
        return (newHero.DamageAdj = 0) && (newHero.HitAdj = 0);
    }
    if (newHero.Str == 17 ) {
        return (newHero.DamageAdj = 1) && (newHero.HitAdj = 1);
    }
    if (newHero.Str == 18 ) {
        return (newHero.DamageAdj = 2) && (newHero.HitAdj = 2);
    }
    if (newHero.Str == 19 ) {
        return (newHero.DamageAdj = 3) && (newHero.HitAdj = 3);
    }
    if (newHero.Str == 20 ) {
        return (newHero.DamageAdj = +4) && (newHero.HitAdj = +4);
    }
}
function setacAdj() {
    if (newHero.Dext >= 1 && newHero.Dext < 3) {
        return AcAdj.one;
    }
    if (newHero.Dext == 3) {
        return AcAdj.two;
    }
    if (newHero.Dext == 4) {
        return AcAdj.three;
    }
    if (newHero.Dext == 5) {
        return AcAdj.four;
    }
    if (newHero.Dext == 6) {
        return AcAdj.five;
    }
    if (newHero.Dext >= 7 && newHero.Dext <= 14) {
        return AcAdj.six;
    }
    if (newHero.Dext == 15) {
        return AcAdj.seven;
    }
    if (newHero.Dext == 16) {
        return AcAdj.eight;
    }
    if (newHero.Dext == 17) {
        return AcAdj.nine;
    }
    if (newHero.Dext == 18 && newHero.Dext == 19) {
        return AcAdj.ten;
    }
    if (newHero.Dext == 20) {
        return AcAdj.eleven;
    }
    if (newHero.Dext = Number.NaN) {
        return AcAdj.six;
    }
    
}

function SetConstAdj() {
    if (newHero.Const == 1) {
        return Constadj.one;
    }
    if (newHero.Const == 2 && newHero.Const == 3) {
        return Constadj.two;
    }
    if (newHero.Const >= 4 && newHero.Const <= 6) {
        return Constadj.three;
    }
    if (newHero.Const >= 7 && newHero.Const <= 14) {
        return Constadj.four;
    }
    if (newHero.Const ==15) {
        return Constadj.five;
    }
    if (newHero.Const == 16) {
        return Constadj.six;
    }
    if (newHero.Const == 17) {
        return Constadj.seven;
    }
    if (newHero.Const == 18) {
        return Constadj.eight;
    }
    if (newHero.Const == 19) {
        return Constadj.nine;
    }
    if (newHero.Const == 20) {
        return Constadj.ten;
    }

    if (newHero.Const = Number.NaN) {
        return Constadj.four;
    }
}

function genRandom(min, max) {
    return Math.floor(Math.random() * (20 - 1 + 1)) + 1;
  }

class Hero extends React.Component {
    constructor(props) {
        super(props);
        this.handleClick = this.handleClick.bind(this);
    }

    handleClick() {
        newHero.Str = 20;
        newHero.Const = genRandom(1, parseInt(numInput.value, 10));
        newHero.Dext = genRandom(1, parseInt(numInput.value, 10));
        //newHero.Dext = Math.floor(Math.random() * (20 - 1 + 1)) + 1;
        HeroHp = (newHero.BaseHitPoints) + (SetConstAdj());
        HeroAc = (newHero.BaseArmorClass) + (setacAdj());
        setDmgAdj();
        this.forceUpdate();
    }

    render() {
        return (
            <div>
            <div>
                <p>Strength: {newHero.Str}</p>
                <p>Constitution: {newHero.Const}</p>
                <p>Dexterity: {newHero.Dext}</p>
                <p>Hitpoints: {HeroHp}</p>
                <p>Armor Class: {HeroAc}</p>
                <p>Damage Adjust: {newHero.DamageAdj}</p>
                <p>Hit Adjust: {newHero.HitAdj}</p>
            </div>
            <div>
            <button onClick={this.handleClick}>Set Attrubutes</button>
            </div>
            </div>
        );
    }
}

export default Hero;
