import { Card, CardImg, CardText, CardBody, CardTitle, Row, Col } from 'reactstrap'
import {AboutImg } from './jsxstyles'

export class AboutMedia extends React.Component{
    render() {
        if (this.props.Align === "Right") {
            return (
                <div className="p-3">
                    <Card className="text-center">
                        <Row>
                            <Col >
                            <CardImg style={AboutImg} src={this.props.ImgUrl} alt="Card image cap" />
                            </Col>
                        <Col >
                        <CardTitle><h1>{this.props.Title}</h1></CardTitle>
                        <CardBody>
                            <CardText>
                                {this.props.Description}
                            </CardText>
                        </CardBody>
                        </Col>
                        </Row>
                    </Card> 
                </div>
            );
        }
        if (this.props.Align === "Left") {
            return (
                <div className="p-3">
                <Card className="text-center">
                    <Row>
                        <Col >
                        <CardTitle><h1>{this.props.Title}</h1></CardTitle>
                        <CardBody>
                            <CardText>
                                {this.props.Description}
                            </CardText>
                        </CardBody>
                        </Col>
                        <Col >
                            <CardImg style={AboutImg} src={this.props.ImgUrl} alt="Card image cap" />
                        </Col>
                    </Row>
                </Card> 
            </div>
            );
        };
    }
}
