import Header from './Header'
import Footer from './Footer'
import '../components/style.css'

const layoutStyle = {
    margin: 20,
    padding: 20,
    flex: 1,
}

const headerStyle = {
    width: '100%',
    height: '50px',
    border: '1px solid #DDD',
}


const bodyStyle = {
    border: '1px solid #DDD',
    display: 'flex',
    flexdirection: 'column',
    minheight: '100vh',
}

const footerStyle = {
    width: '100%',
    bottom: 0,
    height: 50,
    background: 'red',
    color: 'white',
    textAlign: 'center',
}

export default function Layout(props) {
    return (
        <div id="header">
            <div className="Header" style={headerStyle}>
                <Header />
            </div>
            <div style={bodyStyle}>
              {props.children}
            </div>
            <div className="Footer" style={footerStyle}>
            <Footer />
            </div>
        </div>
    )
}
