import React from 'react'
import { newHero } from './hero'
import { HeroHp } from './hero'
import { HeroAc } from './hero'
import { HeroDmg } from './hero'
//import { HeroHitAdj } from './hero'


var monsterhp = 30;
var monsterac = 10;
var roll = 0;
var HeroHitAdj = -1;
var didhit = " ";

function attackroll () {
    roll = Math.floor((Math.random() * 20) + 1);
    if (roll >= (monsterac + HeroHitAdj)) {
        didhit = "yes"
    };
    didhit = "no"
};

class HeroAttackSim extends React.Component {
    render() {
        return (
            <div>
                <p>MonsterAC = { monsterac }</p>
                <p>Hero Hit Adjustment = { HeroHitAdj }</p>
                <button onClick={attackroll()} onMouseUp={this.forceUpdate()}>
                    Roll to hit
                </button>
                <p>Result of attack = { didhit }</p>
            </div>
        );
    }
}

export default HeroAttackSim
