import React from 'react'
import { Row, Col, Table } from 'reactstrap'
import { Dropdown, DropdownToggle, DropdownMenu, DropdownItem } from 'reactstrap';
import { CenterFlexWrapDiv } from '../customComponents'

var data2 = require('./generated.json');
var pizza = require('./drivers.json');

function dateCheck(props) {
    let date = this.props.Date
    if (date < "2019-9-22") {
        return (
            <span className="bg-danger text-white">{date}</span>
        )
    }
    if (date < "2019-10-22") {
        return (
            <span className="bg-warning text-white">{date}</span>
        )
    }
    return (
        <span>{date}</span>
    );
}

class Example extends React.Component {
    constructor(props) {
      super(props);
      
      this.handleChange = this.handleChange.bind(this);
      this.toggle = this.toggle.bind(this);
      this.state = {
        dropdownOpen: false,
        value: 'ASSURITY'
      };
    }
  
    toggle() {
      this.setState(prevState => ({
        dropdownOpen: !prevState.dropdownOpen
      }));
    }

    handleChange(e) {
        this.setState({value: event.target.value});
    }

    render() {
        const test = data2.filter(data2 => data2.company === this.state.value);
        const test2 = test[0];
        

        return (
            <div className="ml-5 mt-5">
                <Dropdown isOpen={this.state.dropdownOpen} toggle={this.toggle}>
                    <DropdownToggle caret>
                        Provider List
                    </DropdownToggle>
                    <DropdownMenu
                        modifiers={{
                        setMaxHeight: {
                            enabled: true,
                            order: 890,
                            fn: (data) => {
                            return {
                                ...data,
                                styles: {
                                ...data.styles,
                                overflow: 'auto',
                                maxHeight: 200  ,
                                },
                            };
                            },
                        },
                        }}
                    >
                        {
                            data2.sort((a, b) => {
                                const One = a.company.toUpperCase();
                                const Two = b.company.toUpperCase();
                            
                                return (One < Two) ? -1 : (One > Two) ? 1 : 0;
                            })
                            .map(TP => (
                                <DropdownItem className="Btn" key={TP.company} onClick={this.handleChange}
                                value={TP.company}>
                                    {TP.company}
                                </DropdownItem>
                            ))
                        }
                    </DropdownMenu>
                </Dropdown>
                <div>
                



        
                <p>Provider: {this.state.value}</p>
                <p><span className="ml-5">email: {test2.email}, </span>
                <span className="ml-5">phone: {test2.phone}, </span>
                <span className="ml-5">address: {test2.address}, </span>
                </p>
                <p>Insurance:</p>
                {
                    test2.insurance.map(insurance => (
                        <span className="ml-5" key={insurance.type}>
                            <span className="ml-5">{insurance.type}</span>
                            <span className="ml-5">{insurance.company}</span>
                            <span className="ml-5">{insurance.end}</span>
                        </span>
                    ))
                }
                <h2 className="mt-5">Drivers:</h2>

                


                {
                    test2.drivers.map(driver => (
                        <div>
                        <Row xs="auto" className="ml-5 p-0" key={driver.id}>
                            <div className="d-inline-block h-25 text-center mx-5">
                                <h3>{driver.name}</h3>
                            </div>
                            <div className="d-inline-block h-25 text-center mx-5 pt-2">
                                <p>License State: {driver.license.state}</p>
                            </div>
                            <div className="d-inline-block h-25 text-center mx-5 pt-2">
                                <p>License Exp: 
                                    {driver.license.exp < "2019-09-22" && 
                                        <span className="bg-danger text-white">{driver.license.exp}</span> ||
                                        <span>{driver.license.exp}</span>
                                    }
                                </p>
                            </div>
                        </Row>
                        
                        <Row >
                        <div>
                            <p>Training:</p>
                            <Table bordered>
                                <thead>
                                    <tr>
                                        <th>Type:</th>
                                        <th>Company:</th>
                                        <th>Exp date</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {driver.training.map(tr => (
                                        <tr key={tr.type}>
                                            <td>Type: {tr.type}: </td>
                                            <td>{tr.company}</td>
                                            <td>
                                                <dateCheck 
                                                    Date = {tr.end}
                                                />
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </Table>
                        </div>
                        <div className="ml-5">
                            <p>Screening:</p>
                            <Table bordered>
                                <thead>
                                    <tr>
                                        <th>Type:</th>
                                        <th>Exp date</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {driver.screenings.map(scr => (
                                        <tr key={scr.type}>
                                            <td>{scr.type}</td>
                                            <td>{scr.end}</td>
                                        </tr>
                                    ))}
                                </tbody>
                            </Table>
                            
                        </div>
                        </Row>
                        </div>
                    ))
                }
                </div>
            </div>
        );
    }
}

export default Example;
