import React, { Component } from 'react';
import { createFilter } from './Filter';
import { createSorter } from './Sort';
//import data from './something.json';

var data = require('./something.json');

var test1 = 'company';
var test2 = 'id';

class List extends Component {
  constructor(props) {
    super(props);
    this.state = {
      option1: test1,
      option2: test2,
    };
  this.handleChange = this.handleChange.bind(this);
  this.handlesubmit = this.handlesubmit.bind(this);
  this.state = {
    data,
    sorter: this.props.sorter
  };
}

  static defaultProps = {
    sorter: [{
      property: test1
    }, {
      property: test2
    }]
  };
  
  handleChange(event) {
    this.setState({option1: event.target.value});
    test1 = (this.state.option1);
}

handlesubmit(event) {
  this.setState({option1: event.target.value});
  test1 = (this.state.option1);
  this.forceUpdate();
}

  componentDidMount () {
    
    fetch(`/data.json`)
      .then(res => res.json())
      .then(this.onLoad);
  }

  

  render () {
    
     const  {sorter}  = this.state;
  
      if (data && data.length > 0) {
        if (Array.isArray(sorter) && sorter.length) {
          data.sort(createSorter(...sorter));
        }
      }
  
      return (
        <div>
        <div>
                    <form>
                        <label>
                            Name: 
                            <input 
                                type="text"
                                maxLength="20"
                                value={this.state.value}
                                onChange={this.handleChange}
                                onSubmit={this.handlesubmit}
                            />
                        </label>
                    </form>
                </div>
        <div>
          {
            data.map(item => (
              <div key={item.id}>
                {item.id} <br />
                {item.company}
                <a href={`mailto:${item.email}`}>{item.name}</a> {item.company}
              </div>
            ))
          }
        </div>
        </div>
      );
    } 
    }
  
  
export default List;
