import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Collapse, Navbar, NavbarToggler, Nav, NavItem, NavLink, UncontrolledDropdown, DropdownToggle, DropdownMenu, DropdownItem } from 'reactstrap';



var activeprofile = ''; 

                

class Header extends React.Component {
constructor(props) {
    super(props);

    this.toggle = this.toggle.bind(this);
    this.state = {
    isOpen: false
    };
}
    toggle() {
    this.setState({
    isOpen: !this.state.isOpen
    });
}
    render() {
        return (
        <div>
            <Navbar  color="dark" dark expand="md">
            <NavbarToggler onClick={this.toggle} />
            <Collapse isOpen={this.state.isOpen} navbar>
                <Nav className="ml-auto Header" navbar>
                    <NavItem>
                        <NavLink className="Header" href="/">Home</NavLink>
                    </NavItem>
                    <NavItem>
                        <NavLink href="/about">About</NavLink>
                    </NavItem>
                    <UncontrolledDropdown nav inNavbar>
                        <DropdownToggle nav >
                            Projects
                        </DropdownToggle>
                        <DropdownMenu right className='bg-dark Header'>
                            <NavItem>
                                <NavLink  className="center" href="/tvpage">TvMaze</NavLink>
                            </NavItem>
                            <DropdownItem>
                                <a href="/DandDpage">DandD</a>
                            </DropdownItem>
                            <DropdownItem>
                                <a href="/ProviderData">Provider List</a>
                            </DropdownItem>
                            <DropdownItem>
                                {activeprofile === 'Guest' &&
                                    <a id="/test">Test</a> 
                                }
                            </DropdownItem>
                        </DropdownMenu>
                    </UncontrolledDropdown>
                </Nav>
            </Collapse>
            <style jsx> {`
                    a, a:hover {
                        color: 'white;
                    }
                `}
                </style>
            </Navbar>
            
        </div>
        );
    }
}


