import Header from './Header'
import Footer from './Footer'

const layoutStyle = {
    margin: 20,
    padding: 20,
}

const headerStyle = {
    width: '100%',
    height: '50px',
    border: '1px solid #DDD',
}


const footerStyle = {
    position: 'fixed',
    width: '100%',
    bottom: 0,
    height: 50,
    background: 'red',
    color: 'white',
    textAlign: 'center',
}

export default function Layout(props) {
    return (
        <div style={layoutStyle}>
            <div className="Header" style={headerStyle}>
                <Header />
            </div>
              {props.children}
            <div className="Footer" style={footerStyle}>
            <Footer />
            </div>
        </div>
    )
}
