import React, { Component } from 'react';
import { createFilter } from './Filter';
import { createSorter } from './Sort';


var data = require('./something.json');
var newdata = data.slice().sort();








class List extends Component {
  constructor(props) {
    super(props);
    this.state = { };
  }

  componentDidMount () {
    fetch('./something.json')
      .then(res => res.json())
      .then(this.onLoad);
  }

  parseData (response) {
    return response;
  }

  onLoad = (newdata) => {
    this.setState({
      newdata: this.parseData(newdata)
    });
  }

  render () {
    
    return newdata ?
      this.renderData(newdata) :
      this.renderLoading()
  }

  renderData (newdata) {
    if (newdata && newdata.length) {
      return (
        <div>
          {
            newdata.map(item => (
              <div key={item.id}>
                <a href={`mailto:${item.email}`}>{item.name}</a> {item.company}
              </div>
            ))
          }
        </div>
      );
    } else {
      return <div>No items found</div>
    }
  }

  renderLoading () {
    return <div>Loading...</div>
  }
}

export default List;
