import React from 'react'



var newHero = {
    Str: 0,
    Const: 0,
    Dext: 0,
    HitPoints: 20,
    BaseArmorClass: 10,
};

var AdjustedHp = 0;
var ArmorClass = 0;


var Constadj = { 
    one: -3, two: -2, three: -1, four: 0,
    five: 1, six: 2, seven: 3, eight: 4, nine: 5, ten: 6
};

var AcAdj = {
    one: -5, two: -4, three: -3, four: -2, five: -1, six: 0,
    seven: 1, eight: 2, nine: 3, ten: 4, eleven: 5
};


function setHpAdj() {
    if (newHero.Dext >= 1 && newHero.Dext < 3) {
        return AcAdj.one;
    }
    if (newHero.Dext == 3) {
        return AcAdj.two;
    }
    if (newHero.Dext == 4) {
        return AcAdj.three;
    }
    if (newHero.Dext == 5) {
        return AcAdj.four;
    }
    if (newHero.Dext == 6) {
        return AcAdj.five;
    }
    if (newHero.Dext >= 7 && newHero.Dext <= 14) {
        return AcAdj.six;
    }
    if (newHero.Dext == 15) {
        return AcAdj.seven;
    }
    if (newHero.Dext == 16) {
        return AcAdj.eight;
    }
    if (newHero.Dext == 17) {
        return AcAdj.nine;
    }
    if (newHero.Dext == 18 && newHero.Dext == 19) {
        return AcAdj.ten;
    }
    if (newHero.Dext == 20) {
        return AcAdj.eleven;
    }
}

function SetConstAdj() {
    if (newHero.Const == 1) {
        return Constadj.one;
    }
    if (newHero.Const == 2 && newHero.Const == 3) {
        return Constadj.two;
    }
    if (newHero.Const >= 4 && newHero.Const <= 6) {
        return Constadj.three;
    }
    if (newHero.Const >= 7 && newHero.Const <= 14) {
        return Constadj.four;
    }
    if (newHero.Const ==15) {
        return Constadj.five;
    }
    if (newHero.Const == 16) {
        return Constadj.six;
    }
    if (newHero.Const == 17) {
        return Constadj.seven;
    }
    if (newHero.Const == 18) {
        return Constadj.eight;
    }
    if (newHero.Const == 19) {
        return Constadj.nine;
    }
    if (newHero.Const == 20) {
        return Constadj.ten;
    }
}

class Hero extends React.Component {
    constructor(props) {
        super(props);
        this.handleClick = this.handleClick.bind(this);
    }

    handleClick() {
        newHero.Str = Math.floor(Math.random() * 20) + 1;
        newHero.Const = Math.floor(Math.random() * 20) + 1;
        newHero.Dext = Math.floor(Math.random() * 20) + 1;
        AdjustedHp = (newHero.HitPoints) + (SetConstAdj());
        ArmorClass = (BaseArmorClass) + (setHpAdj());
        this.forceUpdate();
    }

    render() {
        return (
            <div>
            <div>
                <p>Strength: {newHero.Str}</p>
                <p>Constitution: {newHero.Const}</p>
                <p>Dexterity: {newHero.Dext}</p>
                <p>Hitpoints: {AdjustedHp}</p>
                <p>Armor Class: {ArmorClass}</p>
            </div>
            <div>
            <button onClick={this.handleClick}>Set Attrubutes</button>
            </div>
            </div>
        );
    }
}

export default Hero;
