import React, { Component } from 'react';
import { createFilter } from './Filter';
import { createSorter } from './sort';
//import data from './something.json';

var {data} = require('./something.json');

class List extends Component {
  state = {
    data,
    sorter: this.props.sorter
  };

  static defaultProps = {
    sorter: [{
      property: 'name'
    }, {
      property: 'company'
    }]
  };

  componentDidMount() {
    fetch('/something.json')
      .then(res => res.json())
      .then(this.onLoad);
  }

  parseData(data) {
    const { sorter } = this.state;

    if (data && data.length && Array.isArray(sorter) && sorter.length) {
      data.sort(createSorter(...sorter));
    }

    return data;
  }

  onLoad = data => {
    this.setState({
      data: this.parseData(data)
    });
  };

  render() {
    const  {data}  = this.parseData(data);

    return data ? this.renderData(data) : this.renderLoading();
  }

  renderData(data) {
    if (data && data.length > 0) {
      return (
        <div>
          {data.map(item => (
            <div key={item.id}>
              <a href={`mailto:${item.email}`}>{item.name}</a> {item.company}
            </div>
          ))}
        </div>
      );
    } else {
      return <div>No items found</div>;
    }
  }

  renderLoading() {
    return <div>Loading...</div>;
  }
}



export default List;    


