import React, { Component } from 'react'
import Layout from '../components/layout'
import CSS from '../components/style.css'
import Layout2 from './tv_info/MyLayout.js'
import Link from 'next/link'
import fetch from 'isomorphic-unfetch'


var test = '';

class SearchBar extends Component {
  constructor(props) {
      super(props);

      this.state = {query: ''};
      this.onInputChange = this.onInputChange.bind(this);
      this.stupid = this.stupid.bind(this);
  }

  onInputChange(event) {
      this.setState({query: event.target.value});
      test = this.state.query;
  }

  stupid(event) {
    this.forceUpdate(TvPage);
  }

  render() {
      return (
          <div className="search-bar">
              <form onSubmit={this.stupid}>
                <label>
                  Search:
                  <input type="text" value={this.state.value} onChange={this.onInputChange} />
                </label>
                <input type="submit" value="Submit" />
              </form>
          </div>
      );
  }
}

const TvPage = props => (
    <Layout>
      <h1 className="center">the below content has been imported from <a href="https://www.tvmaze.com" target="_blank" rel="noopener noreferrer">TVmaze.com</a></h1>
        <SearchBar />
        <Layout2>
          <h1>{test} TV Shows</h1>
            <ul>
              {props.shows.map(show => (
                <li key={show.id}>
                  <Link as={`/p/${show.id}`} href={`/tvpost?id=${show.id}`}>
                    <a>{show.name}</a>
                  </Link>
                </li>
                ))}
            </ul>
        </Layout2>
    </Layout>
  );

  TvPage.getInitialProps = async function() {
    this.state = {search: test};
    function componentWillMount() {
      this.state = {search: test}
    };
    //const Search = test;
    
    const res = await fetch('https://api.tvmaze.com/search/shows?q=' + this.state.search);
    const data = await res.json();
  
    console.log(`Show data fetched. Count: ${data.length}`);
  
    return {
      shows: data.map(entry => entry.show)
    };
  };
  
  export default TvPage;
