import React, { Component } from 'react';
import { createFilter } from './Filter';
import { createSorter } from './sort';

var data = require('./provider.json');

var test1 = {
  region: {
  property: 'Region',
  value: 'Central',
}
};


class ProviderList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      filters: this.props.filters,
      sorter: this.props.sorter,
      sortingProp: 'item.Type',
      sortingDirectionAsc: true
  };
}


  static defaultProps = {
    filters: [{
      property: 'Type',
      value: ''
    }, {
      property: test1.region.property,
      value: test1.region.value
    }, {
      property: 'County',
      value: ''
    }, {
      property: 'WCHR',
      value: ''
    }, {
      property: 'Stretcher',
      value: 'Yes'
    }],
    sorter: [{
      property: 'County'
    }, {
      property: 'name'
    }]
  };
  
  componentDidMount () {
    fetch(`/provider.json`)
      .then(res => res.json());
       
  }

  parseData(data) {
    const { sorter } = this.state;

    if (data && data.length) {
      if (Array.isArray(sorter) && sorter.length) {
        data.sort(createSorter(...sorter));
      }
    }

    return data;
  }

  componentWillReceiveProps(nextProps) {
    this.setState({
      sortingProp: 'item.Type',
      sortingDirectionAsc: true
    })
  }

  sortAsc(prop) {
    return this.state.items.sort((a, b) => {
      if (a[prop] > b[prop]) {
        return 1;
      }

      if (a[prop] < b[prop]) {
        return -1;
      }

      return 0;
    })
  }
  
  sortDesc(prop) {
    return this.state.items.sort((a, b) => {
      if (a[prop] < b[prop]) {
        return 1;
      }

      if (a[prop] > b[prop]) {
        return -1;
      }

      return 0;
    })
  }
  
  sortBy(prop) {
    if (prop === this.state.sortingProp) {
      if (this.state.sortingDirectionAsc) {
        this.setState({
          sortingDirectionAsc: false,
          items: this.sortDesc(prop)
        })
        return
      } else {
        this.setState({
          sortingDirectionAsc: true,
          items: this.sortAsc(prop)
        })
        return
      }
    }
    this.setState({
      sortingProp: prop,
      sortingDirectionAsc: true,
      items: this.sortAsc(prop)
    })
  }

  render() {
    //const  data   = this.parseData(data);
    return this.parseData(data) ? this.renderData(data) : this.renderLoading();
  }

  renderData(data) {
    const { filters } = this.state;
    
    if (data && data.length > 0) {
      if (Array.isArray(filters) && filters.length) {
        data = data.filter(createFilter(...filters));
      }
      return (
        <div>
          
            
              <table 
              
              className="Provider-table">
              <tr>
                <th 
                 onClick={this.sortBy.bind(this, 'Type')}>Type</th>
                <th>Provider Name</th>
                <th>Provider Region</th>
                <th>County</th>
                <th>Ambulatory</th>
                <th>Wheelchair</th>
                <th>Stretcher</th>
              </tr>
              {data.map(item => (
                <tr key={item.id}>
                  <td>{item.Type}</td>
                  <td>{item.Name}</td>
                  <td>{item.Region}</td>
                  <td>{item.County}</td>
                  <td>{item.Amb}</td>
                  <td>{item.WCHR}</td>
                  <td>{item.Stretcher}</td>
                </tr>
              ))}
            </table>
          
        </div>
      );
    } else {
      return <div>No items found</div>;
    }
  }

  renderLoading() {
    return <div>Loading...</div>;
  }
}

export default ProviderList;


