import React, { Component } from 'react';
import { createFilter } from './Filter';
import { createSorter } from './Sort';
//import data from './something.json';

var data = require('./something.json');



class List extends Component {
  constructor(props) {
    super(props);
  this.state = {
    data,
    sorters: this.props.sorters
  };
}
  static defaultProps = {
    sorters: [{
      property: 'name'
    }, {
      property: 'company'
    }]
  };
  
  


  componentDidMount () {
    
    fetch(`/data.json`)
      .then(res => res.json())
      .then(this.onLoad);
  }

  

  render () {
    const {data} = this.state;
    if (data && data.length) {
      
      

      return (
        <div>
          {
            data.map(item => (
              <div key={item.id}>
                {item.id} <br />
                {item.company}
                <a href={`mailto:${item.email}`}>{item.name}</a> {item.company}
              </div>
            ))
          }
        </div>
      );
    } else {
      return <div>you suck!</div>;
    }
  }
  };
export default List;
