import React, { Component } from 'react'
import { newHero } from './hero'


export function SetConstAdj() {
    if (newHero.Const == 1) {
        return -3;
    }
    if (newHero.Const == 2 || newHero.Const == 3) {
        return -2;
    }
    if (newHero.Const >= 4 && newHero.Const <= 6) {
        return -1;
    }
    if (newHero.Const >= 7 && newHero.Const <= 14) {
        return 0;
    }
    if (newHero.Const == 15) {
        return 1;
    }
    if (newHero.Const == 16) {
        return 2;
    }
    if (newHero.Const == 17) {
        return 3;
    }
    if (newHero.Const == 18) {
        return 4;
    }
    if (newHero.Const == 19) {
        return 5;
    }
    if (newHero.Const == 20) {
        return 6;
    }
}
