import React, { Component } from 'react';
import { createFilter } from './Filter';
import { createSorter } from './Sort';
//import data from './something.json';

var data = require('./something.json');



class List extends Component {
  constructor(props) {
    super(props);
  this.state = {
    data,
    sorters: this.props.sorters
  };
}
  static defaultProps = {
    sorters: [{
      property: 'company'
    }, {
      property: 'name'
    }]
  };
  
  


  componentDidMount () {
    
    fetch(`/data.json`)
      .then(res => res.json())
      .then(this.onLoad);
  }

  

  render () {
    
      const  sorters  = this.state;
  
      if (data && data.length > 0) {
        if (Array.isArray(sorters) && sorters.length) {
          data.sort(createSorter(...sorters));
        }
      }
  
      return (
        <div>
          {
            data.map(item => (
              <div key={item.id}>
                {item.id} <br />
                {item.company}
                <a href={`mailto:${item.email}`}>{item.name}</a> {item.company}
              </div>
            ))
          }
        </div>
      );
    } 
    }
  
  
export default List;
