import React from 'react'



var Hero = {
    Str: Math.floor(Math.random() * 20) + 1,
    Const: 1,
    Dext: Math.floor(Math.random() * 20) + 1
}
/*
var strength = Math.floor(Math.random() * 20) + 1;
var constitution = 0;
var dexterity = 0;

const attributes = {
    strength: '20',
    constitution: '0',
    dexterity: '0'
}*/

var Adj = {
    HpAdj : function() {
        if (Hero.Const = 1) {
            return -3;
        }
    } 
}


class displayHero extends React.Component {
    render() {
        return (
            <div>
                <p>Strength: {Hero.Str}</p>
                <p>Constitution: {Hero.Const}</p>
                <p>Dexterity: {Hero.Dext}</p>
                <p>Hit Point adjust: {Adj.HpAdj}</p>

            </div>
        );
    }
}

export default displayHero;
