import React from 'react';



var newHero = {
    Str: 0,
    Const: 0,
    Dext: 0,
    BaseHitPoints: 20,
    BaseArmorClass: 10,
    BaseDamage: 10,
    BaseHitAdj: 0
};

var HeroHp = 0;
var HeroAc = 0;
var HeroDmg = 0;
var HeroHitAdj = 0;

function setHitAdj() {
    if (newHero.Str == 1) {
        return -4;
    }
    if (newHero.Str == 2 || newHero.Str == 3) {
        return -3;
    }
    if (newHero.Str == 4 || newHero.Str == 5) {
        return -2;
    }
    if (newHero.Str == 6 || newHero.Str == 7) {
        return -1;
    }
    if (newHero.Str >= 8 && newHero.Str <= 16) {
        return 0;
    }
    if (newHero.Str == 17 ) {
        return 1;
    }
    if (newHero.Str == 18 ) {
        return 2;
    }
    if (newHero.Str == 19 ) {
        return 3;
    }
    if (newHero.Str == 20 ) {
        return 4;
    }
}

function setDmgAdj() {
    if (newHero.Str == 1) {
        return -4;
    }
    if (newHero.Str == 2 || newHero.Str == 3) {
        return -3;
    }
    if (newHero.Str == 4 || newHero.Str == 5) {
        return -2;
    }
    if (newHero.Str == 6 || newHero.Str == 7) {
        return -1;
    }
    if (newHero.Str >= 8 && newHero.Str <= 16) {
        return 0;
    }
    if (newHero.Str == 17 ) {
        return 1;
    }
    if (newHero.Str == 18 ) {
        return 2;
    }
    if (newHero.Str == 19 ) {
        return 3;
    }
    if (newHero.Str == 20 ) {
        return 4;
    }
}

function setacAdj() {
    if (newHero.Dext == 1 || newHero.Dext == 2) {
        return -5;
    }
    if (newHero.Dext == 3) {
        return -4;
    }
    if (newHero.Dext == 4) {
        return -3;
    }
    if (newHero.Dext == 5) {
        return -2;
    }
    if (newHero.Dext == 6) {
        return -1;
    }
    if (newHero.Dext >= 7 && newHero.Dext <= 14) {
        return 0;
    }
    if (newHero.Dext == 15) {
        return 1;
    }
    if (newHero.Dext == 16) {
        return 2;
    }
    if (newHero.Dext == 17) {
        return 3;
    }
    if (newHero.Dext == 18 || newHero.Dext == 19) {
        return 4;
    }
    if (newHero.Dext == 20) {
        return 5;
    }
}

function SetConstAdj() {
    if (newHero.Const == 1) {
        return -3;
    }
    if (newHero.Const == 2 || newHero.Const == 3) {
        return -2;
    }
    if (newHero.Const >= 4 && newHero.Const <= 6) {
        return -1;
    }
    if (newHero.Const >= 7 && newHero.Const <= 14) {
        return 0;
    }
    if (newHero.Const == 15) {
        return 1;
    }
    if (newHero.Const == 16) {
        return 2;
    }
    if (newHero.Const == 17) {
        return 3;
    }
    if (newHero.Const == 18) {
        return 4;
    }
    if (newHero.Const == 19) {
        return 5;
    }
    if (newHero.Const == 20) {
        return 6;
    }
}





class Hero extends React.Component {
    constructor(props) {
        super(props);
        this.handleClick = this.handleClick.bind(this);
    }

    handleClick() {
        newHero.Str = Math.floor((Math.random() * 20) + 1);
        newHero.Const = Math.floor((Math.random() * 20) + 1);
        newHero.Dext = Math.floor((Math.random() * 20) + 1);
        HeroHp = (newHero.BaseHitPoints) + (SetConstAdj());
        HeroAc = (newHero.BaseArmorClass) + (setacAdj());
        HeroDmg = (newHero.BaseDamage) + (setDmgAdj());
        HeroHitAdj = (newHero.BaseHitAdj) + (setHitAdj());
        this.forceUpdate();
    }

    
    render() {
        return (
            <div>
            <div>
                <p>Strength: {newHero.Str}</p>
                <p>Constitution: {newHero.Const}</p>
                <p>Dexterity: {newHero.Dext}</p>
                <p>Hitpoints: {HeroHp}</p>
                <p>Armor Class: {HeroAc}</p>
                <p>Base Damage: {HeroDmg  }</p>
                <p>Hit Adjust: {HeroHitAdj}</p>
            </div>
            <div>
            <button onClick={this.handleClick}>Set Attrubutes</button>
            </div>
            </div>
        );
    }
}

export default Hero;
