import Layout from '../components/layout';
import Layout2 from './tv_info/MyLayout';
import fetch from 'isomorphic-unfetch';
import Link from 'next/link'
import { Button, Form, FormGroup, Label, Input, Card, CardImg, CardBody, CardTitle } from 'reactstrap'
const noimage = '/static/images/no-img.png';

var Search = '';



class Searchbar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {value: ''};
    this.enterSearch = this.enterSearch.bind(this);
  }
  //handles setting state of search value to render tvmaze json api
  //sets variable search value to state
  enterSearch(event) {
    this.setState({value: event.target.value});
    Search = (event.target.value);
  }


  render() {
    return (
      <div className="d-flex justify-content-center ">
        {/* currentl does not have on submit or entery key function to push link to 
          page for api call to render*/}
        <Form inline>
          <FormGroup >
            <Label className="mr-sm-2">TV Show search by Name:</Label>
            <Input
              type="text"
              value={this.state.value} 
              onChange={this.enterSearch} 
              //Prevent enter key submit
              onKeyPress={event => {
                if (event.which === 13 /* Enter */) {
                  event.preventDefault();
                }
              }}
            />
            
          </FormGroup>
          {/*due to get initial props async function is only called on page load
            disguised a page link as a search button to call value of search for 
            the await fetch api call.*/}
          <Button className="ml-4 btn-dark">
            <Link href="/tvpage">
              <a className="btn-dark w-100">Click Submit</a>
            </Link>
          </Button>
        </Form>
      </div>
    );
  }
}

const tvPost = props => (
  <Layout>
    <h1 className="text-center">the below content has been imported from <a href="https://www.tvmaze.com" target="_blank" rel="noopener noreferrer">TVmaze.com</a></h1>
    <Searchbar />
    <Layout2>
      <div >
        <h1>{props.show.name}</h1>
        <p><b>Type:</b> {props.show.type ? props.show.type : 'no data'}</p>
        <p><b>Premiered:</b> {props.show.premiered ? props.show.premiered : 'no data'}</p>
        <p><b>status:</b> {props.show.status ? props.show.status : 'no data'}</p>
        <p><b>Rating:</b> {props.show.rating.average ? props.show.average : 'no data'}</p>
        <p>
          {/* since tvmaze api json has genre as an imbedded array,
            pulls the data in genres in a key map to list all entries*/}
          <b>Genre:</b> &nbsp;
          {props.show.genres.map(genre => (
            <span key={props.show.genres}>
            {genre} &nbsp;
            </span>
          ))}
        </p>
        <img src={props.show.image ? props.show.image.medium : noimage} alt="no image"/>
        <p>{props.show.summary.replace(/<[/]?p>/g, '').replace(/<[/]?b>/g, '').replace(/<[/]?i>/g, '')}</p>
        <p>Link: 
          <a href={props.show.url}
              target="_blank"
              rel="noopener noreferrer"
              title={props.show.url}>
          {props.show.url ? props.show.url : 'null'}
          </a>
        </p>
      </div>
    </Layout2>
  </Layout>
);

tvPost.getInitialProps = async function(context) {
  const { id } = context.query;
  //pules tvmaze api json based on selected show on the search page.
  const res = await fetch(`https://api.tvmaze.com/shows/${id}`);
  const show = await res.json();
  
  console.log(show);

  console.log(`Fetched show: ${show.name}`);

  return { show };
};

export default tvPost;
