import React, { Component } from 'react'
import Layout from '../components/layout'
//import About from './about_view/aboutview'
//import Resume from './about_view/resumeview'

function openCity(evt, cityName) {
    var i, tabcontent, tablinks;
    tabcontent = document.getElementsByClassName("tabcontent");
    for (i = 0; i < tabcontent.length; i++) {
        tabcontent[i].getElementsByClassName.display = "none";
    }
    tablinks = document.getElementsByClassName("tablinks");
    for (i = 0; i < tablinks.length; i++) {
        tablinks[i].className = tablinks[i].className.replace(" active", "");
    }
    document.getElementById(cityName).style.display = "block";
    evt.currentTarget.className += " active";
}

class Aboutpage extends React.Component {
    render() {
        return (
            <Layout>
                <div className="about_header">
                    <div className="about_nav center">
                        <div className="tab">
                            <button className="tablinks" onClick="openCity(event, 'London')">London</button>
                            <button className="tablinks" onClick="openCity(event, 'Paris')">Paris</button>
                            <button className="tablinks" onClick="openCity(event, 'Tokyo')">Tokyo</button>
                        </div>
                        <div id="London" className="tabcontent">
                            <h3>London</h3>
                            <p>London is the capital city of England.</p>
                        </div>
                        <div id="Paris" className="tabcontent">
                            <h3>Paris</h3>
                            <p>Paris is the capital of France.</p>
                        </div>
                        <div id="Tokyo" className="tabcontent">
                            <h3>Tokyo</h3>
                            <p> Tokyo is the capital of Japan.</p>
                        </div>
                    </div>
                </div>
                
            </Layout>
        );
    }
}

export default Aboutpage;
