import React, { Component } from 'react';
import { createFilter } from './Filter';
import { createSorter } from './sort';
//import data from './something.json';

var data = require('./something.json');

  

var test1 = 'name';
var test2 = 'company';

class List extends Component {
  constructor(props) {
    super(props);
    this.state = {
      option1: test1,
      option2: test2,
    };
  
  this.state = {
    data,
    
    sorter: this.props.sorter
  };
}

  static defaultProps = {
    sorter: [{
      property: test1
    }, {
      property: test2
    }]
  };
  
  componentDidMount () {
    fetch(`/data.json`)
      .then(res => res.json())
      .then(this.onLoad);
  }

  onLoad = (data) =>  {
    const  sorters  = this.state;

    if (data && data.length) {
      if (Array.isArray(sorters) && sorters.length) {
        data.sort(createSorter(...sorters));
      }
    }

    return data;
  }

  render() {
    //const  data  = this.state;

    return data ? this.renderData(data) : this.renderLoading();
  }

  renderData(data) {
    const { sorter } = this.state;
    if (data && data.length > 0) {
      //if (Array.isArray(sorter) && sorter.length) {
        //data.sort(createSorter(...sorter));
      //}
      return (
        <div>
          {data.map(item => (
            <div key={item.id}>
              <a href={`mailto:${item.email}`}>{item.name}</a> {item.company}
            </div>
          ))}
        </div>
      );
    } else {
      return <div>No items found</div>;
    }
  }

  renderLoading() {
    return <div>Loading...</div>;
  }
}

export default List;






