import React from 'react'
import { newHero } from './hero'
import { HeroHp } from './hero'
import { HeroAc } from './hero'
import { HeroDmg } from './hero'
import { MonsteradjStats } from './monster'
//import { HeroHitAdj } from './hero'


var NewMonsterHp =  MonsteradjStats.MonsterHp;
var monsterac = 10;
var roll = 0;
var HeroHitAdj = -1;
var didhit = " ";
var MonsteradjHP = NewMonsterHp;


class HeroAttackSim extends React.Component {
    constructor (props) {
        super(props);
        
        const didhit = " ";
        this.attackroll = this.attackroll.bind(this);
    }

    attackroll () {
        roll = Math.floor((Math.random() * 20) + 1);
        if (roll >= (monsterac + HeroHitAdj)) {
            didhit = "yes";
            MonsteradjHP = (MonsteradjHP - 10);
        } else {
            didhit = "no";
        };
        
        this.forceUpdate();
    }

    render() {
        return (
            <div>
                <button onClick={this.forceUpdate()}>Refresh stats</button>
                <p>MonsterAC = { monsterac }</p>
                <p>Hero Hit Adjustment = { HeroHitAdj }</p>
                <button onClick={this.attackroll}>
                    Roll to hit
                </button>
                <p>Result of attack = { didhit }</p>
                <p> Monster hit points = { MonsteradjHP }</p>
            </div>
        );
    }
}

export default HeroAttackSim
