import React, { Component } from 'react';
import { createFilter } from './Filter';



var data = require('./provider.json');

  


class List extends Component {
  constructor(props) {
    super(props);
  this.state = {
    
    
    filters: this.props.filters
  };
}

  static defaultProps = {
    filters: [{
      property: 'Name',
      value: ''
    }, {
      property: 'Region',
      value: 'East'
    }, {
      property: 'WCHR',
      value: ''
    }]
  };
  
  componentDidMount () {
    fetch(`/provider.json`)
      .then(res => res.json());
  }

 

  render() {
    

    return data ? this.renderData(data) : this.renderLoading();
  }

  renderData(data) {
    const { filters } = this.state;
    if (data && data.length > 0) {
      if (Array.isArray(filters) && filters.length) {
        data = data.filter(createFilter(...filters));
      }
      return (
        <div className="Provider-container">
          
          <div> 
              <table 
              className="Provider-table">
                <tr>
              <th>Provider Name</th>
              <th>Provider Region</th>
              <th>City</th>
              <th>County</th>
              <th>Ambulatory</th>
              <th>Wheelchair</th>
              <th>Stretcher</th>
              </tr>
            
              {data.map(item => (
                <tr key={item.id}>
                <td>{item.Name}</td>
                <td>{item.Region}</td>
                <td>{item.City}</td>
                <td>{item.County}</td>
                <td>{item.Amb}</td>
                <td>{item.WCHR}</td>
                <td>{item.Stretcher}</td>
                
              </tr>
              ))}
            </table>
            
            
          
          
        </div>
        </div>
      );
    } else {
      return <div>No items found</div>;
    }
  }

  renderLoading() {
    return <div>Loading...</div>;
  }
}

export default List;






