import React, { Component } from 'react'
import Layout from '../components/layout'
import About from './about_view/about_view'
import Resume from './about_view/resume_view'


class Aboutpage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {showAbout: true}
        this.aboutclick = this.aboutclick.bind(this);

        this.state = {showResume: false}
        this.resumeclick = this.resumeclick.bind(this);

        this.state = {aboutbutton: false}
        this.aboutbuttonclick = this.aboutbuttonclick.bind(this);

        this.state = {resumebutton: true}
        this.resumebuttonclick = this.resumebuttonclick.bind(this);
    }

    aboutclick() {
        this.setState(prevState => ({
            showResume: !prevState.showResume
        }));
        this.setState(prevState => ({
            showAbout: !prevState.showAbout
        }));
    }

    aboutbuttonclick() {
        this.setState(prevState => ({
            aboutbutton: !prevState.aboutbutton
        }))
    }

    resumeclick() {
        this.setState(prevState => ({
            showResume: !prevState.showResume
        })); 
        this.setState(prevState => ({
            showAbout: !prevState.showAbout
        })); 
    }

    resumebuttonclick() {
        this.setState(prevState => ({
            resumebutton: !prevState.resumebutton
        }))
    }

    /*&& this.state.active}*/

    render() {
        return (
            <Layout>
                <div>
                    <p  
                        className="pointer"
                        onClick={this.state.aboutclick}>
                        {this.state.showAbout ? 'About' : 'About'
                        && this.state.aboutbuttonclick} 
                    </p>
                    <p className="pointer"
                        
                        onClick={this.resumeclick && this.state.resumebuttonclick}>
                        {this.state.showResume ? 'Resume' : 'Resume'}
                    </p>
                    
                </div>
                <div>
                <About about={this.state.showAbout}/>
                </div>
                <div>
                    <Resume resume={this.state.showResume}/>
                </div>
            </Layout>
        );
    }
}

export default Aboutpage
