import React from 'react';
import { SetConstAdj } from './herofunctions';
//import { setHitAdj } from './herofunctions';
//import { setDmgAdj } from './herofunctions';
//import { setacAdj } from './herofunctions';
//import { HeroadjStats } from './attacksim';

//export { newHero }
export { Hero }




class Hero extends React.Component {
    constructor(props) {
        super(props);
        this.state = { 
            herostats: {
                Str: 0,
                Const: 0,
                Dext: 0
            },
            herobase: {
                Hp: 50,
                ArmorClass: 10,
                Damage: 10,
                HitAdj: 0
            },
            heroHp: 0,
            heroAc: 0,
            heroDmg: 0,
            heroHitAdj: 0,
        };
        this.setherostats = this.setherostats.bind(this);
        this.SetConstAdj = SetConstAdj.bind(this);
        this.SetacAdj = this.SetacAdj.bind(this);
        this.SetDmgAdj = this.SetDmgAdj.bind(this);
        this.SetHitAdj = this.SetHitAdj.bind(this);
    }

    setherostats() {
        for (let key in this.state.herostats) {
            this.state.herostats[key] = Math.floor((Math.random() * 20) + 1);
        };
        //calls functions in hero_functions to adjust stats in
        //heroadjstats variable in the attacksim.js
        this.setState ({
            heroHp: this.state.herobase.Hp + this.SetConstAdj(),
            heroAC: this.state.herobase.ArmorClass + this.SetacAdj(),
            heroDmg: (this.state.herobase.Damage) + this.SetDmgAdj(),
            heroHitAdj: (this.state.herobase.HitAdj) + 1,
        });
    };



    SetacAdj() {
        if (this.state.herostats.Dext == 1 || this.state.herostats.Dext == 2) {
            return -5;
        }
        if (this.state.herostats.Dext == 3) {
            return -4;
        }
        if (this.state.herostats.Dext == 4) {
            return -3;
        }
        if (this.state.herostats.Dext == 5) {
            return -2;
        }
        if (this.state.herostats.Dext == 6) {
            return -1;
        }
        if (this.state.herostats.Dext >= 7 && this.state.herostats.Dext <= 14) {
            return 0;
        }
        if (this.state.herostats.Dext == 15) {
            return 1;
        }
        if (this.state.herostats.Dext == 16) {
            return 2;
        }
        if (this.state.herostats.Dext == 17) {
            return 3;
        }
        if (this.state.herostats.Dext == 18 || this.state.herostats.Dext == 19) {
            return 4;
        }
        if (this.state.herostats.Dext == 20) {
            return 5;
        }
    }

    SetDmgAdj() {
        if (this.state.herostats.Str == 1) {
            return -4;
        }
        if (this.state.herostats.Str == 2 || this.state.herostats.Str == 3) {
            return -3;
        }
        if (this.state.herostats.Str == 4 || this.state.herostats.Str == 5) {
            return -2;
        }
        if (this.state.herostats.Str == 6 || this.state.herostats.Str == 7) {
            return -1;
        }
        if (this.state.herostats.Str >= 8 && this.state.herostats.Str <= 16) {
            return 0;
        }
        if (this.state.herostats.Str == 17 ) {
            return 1;
        }
        if (this.state.herostats.Str == 18 ) {
            return 2;
        }
        if (this.state.herostats.Str == 19 ) {
            return 3;
        }
        if (this.state.herostats.Str == 20 ) {
            return 4;
        }
    }

    SetHitAdj() {
        if (this.state.herostats.Str == 1) {
            return -4;
        }
        if (this.state.herostats.Str == 2 || this.state.herostats.Str == 3) {
            return -3;
        }
        if (this.state.herostats.Str == 4 || this.state.herostats.Str == 5) {
            return -2;
        }
        if (this.state.herostats.Str == 6 || this.state.herostats.Str == 7) {
            return -1;
        }
        if (this.state.herostats.Str >= 8 && this.state.herostats.Str <= 16) {
            return 0;
        }
        if (this.state.herostats.Str == 17 ) {
            return 1;
        }
        if (this.state.herostats.Str == 18 ) {
            return 2;
        }
        if (this.state.herostats.Str == 19 ) {
            return 3;
        }
        if (this.state.herostats.Str == 20 ) {
            return 4;
        }
    }

    render() {
        return (
            <div className="Hero">
                <div className="HeroBox">
                    <div>

                        <table className="DandDstat_table">
                            <tr>
                                <td>Strength:</td>
                                <td> { this.state.herostats.Str }</td>
                            </tr>
                            <tr>
                                <td>Constitution:</td>
                                <td> { this.state.herostats.Const }</td>
                            </tr>
                            <tr>
                                <td>Dexterity:</td>
                                <td> { this.state.herostats.Dext }</td>
                            </tr>
                            <tr>
                                <td>Hitpoints:</td>
                                <td> { this.state.heroHp }</td>
                            </tr>
                            <tr>
                                <td>Base Damage:</td>
                                <td> { this.state.heroDmg }</td>
                            </tr>
                        </table>
                    </div>
                    <div>
                        <img className="DnD_Hero Float-right" src="../static/images/hero.png" alt="hero"/> 
                    </div>
                </div>
                <div className="Stats-roll align-center">
                    <button onClick={ this.setherostats }>Set Attrubutes</button>
                </div>
                <div>

                </div>
            </div>
        );
    }
}
