import React, { Component } from 'react';
import { createFilter } from './Filter';
import { createSorter } from './Sort';
//import data from './something.json';

var data = require('./something.json');

var test1 = 'name';
var test2 = 'id';

class List extends Component {
  constructor(props) {
    super(props);
    this.state = {
      option1: test1,
      option2: test2,
    };
  this.handleChange = this.handleChange.bind(this);
  this.handlesubmit = this.handlesubmit.bind(this);
  this.state = {
    data,
    sorter: this.props.sorter
  };
}

  static defaultProps = {
    sorter: [{
      property: test1
    }, {
      property: test2
    }]
  };
  


  componentDidMount () {
    
    fetch(`/data.json`)
      .then(res => res.json())
      .then(this.onLoad);
  }

  

  render () {
    
     const  {sorter}  = this.state;
  
      if (data && data.length > 0) {
        if (Array.isArray(sorter) && sorter.length) {
          data.sort(createSorter(...sorter));
        }
      }
  
      return (
        
        <div>
                    
          {
            data.map(item => (
              <div key={item.id}>
                {item.id} <br />
                {item.company}
                <a href={`mailto:${item.email}`}>{item.name}</a> {item.company}
              </div>
            ))
          }
        </div>
        
      );
    } 
    }
  
  
export default List;
