import React, { Component } from 'react'
import Layout from '../components/layout'
import CSS from '../components/style.css'
import Layout2 from './tv_info/MyLayout.js'
import Link from 'next/link'
import fetch from 'isomorphic-unfetch'


var test = '';

class SearchBar extends Component {
  constructor(props) {
      super(props);

      this.state = {query: test};
  }

  onInputChange(query) {
      this.setState({query});
      
  }

  render() {
      return (
          <div className="search-bar">
              <p>Search:
              <input
                  value={this.state.query}
                  onChange={event => this.onInputChange(event.target.value)} />
                  </p>  
          </div>
      );
  }
}

const TvPage = props => (
    <Layout>
      <h1 className="center">the below content has been imported from <a href="https://www.tvmaze.com" target="_blank" rel="noopener noreferrer">TVmaze.com</a></h1>
        <SearchBar />
        <Layout2>
          <h1>{test} TV Shows</h1>
            <ul>
              {props.shows.map(show => (
                <li key={show.id}>
                  <Link as={`/p/${show.id}`} href={`/tvpost?id=${show.id}`}>
                    <a>{show.name}</a>
                  </Link>
                </li>
                ))}
            </ul>
        </Layout2>
    </Layout>
  );

  TvPage.getInitialProps = async function() {
    //const Search = test;
    
    const res = await fetch('https://api.tvmaze.com/search/shows?q=' + test);
    const data = await res.json();
  
    console.log(`Show data fetched. Count: ${data.length}`);
  
    return {
      shows: data.map(entry => entry.show)
    };
  };
  
  export default TvPage;
