//sets hitpoints based on constitution stat number.
export function SetConstAdj(constitution) {
    if (constitution == 1) {
        return -3;
    }
    if (constitution == 2 || constitution == 3) {
        return -2;
    }
    if (constitution >= 4 && constitution <= 6) {
        return -1;
    }
    if (constitution >= 7 && constitution <= 14) {
        return 0;
    }
    if (constitution == 15) {
        return 1;
    }
    if (constitution == 16) {
        return 2;
    }
    if (constitution == 17) {
        return 3;
    }
    if (constitution == 18) {
        return 4;
    }
    if (constitution == 19) {
        return 5;
    }
    if (constitution == 20) {
        return 6;
    }
};

//sets Armorclass based on dexterity stat number.
export function SetAcAdj(dexterity) {
    if (dexterity == 1 || dexterity == 2) {
        return -5;
    }
    if (dexterity == 3) {
        return -4;
    }
    if (dexterity == 4) {
        return -3;
    }
    if (dexterity == 5) {
        return -2;
    }
    if (dexterity == 6) {
        return -1;
    }
    if (dexterity >= 7 && dexterity <= 14) {
        return 0;
    }
    if (dexterity == 15) {
        return 1;
    }
    if (dexterity == 16) {
        return 2;
    }
    if (dexterity == 17) {
        return 3;
    }
    if (dexterity == 18 || dexterity == 19) {
        return 4;
    }
    if (dexterity == 20) {
        return 5;
    }
}

//sets damage and hit adjustment based on strength stat number.
export function SetDmgHitAdj(strength) {
    if (strength == 1) {
        return -4;
    }
    if (strength == 2 || strength == 3) {
        return -3;
    }
    if (strength == 4 || strength == 5) {
        return -2;
    }
    if (strength == 6 || strength == 7) {
        return -1;
    }
    if (strength >= 8 && strength <= 16) {
        return 0;
    }
    if (strength == 17 ) {
        return 1;
    }
    if (strength == 18 ) {
        return 2;
    }
    if (strength == 19 ) {
        return 3;
    }
    if (strength == 20 ) {
        return 4;
    }
}
