import Header from './Header'
import Footer from './Footer'
import 'bootstrap/dist/css/bootstrap.min.css'

const Bodystyle = {
  minHeight: '100vh',
  maxWidth: '98%',
  marginRight: 'auto',
  marginLeft: 'auto',
}

const Layoutstyle = {
  margin: '0px'
}

export default function Layout(props) {
  return (
    <div style={Layoutstyle}>
      <Header />
      <div style={Bodystyle}>
        {props.children}
      </div>
      <Footer />
    </div>
  );
}
